parameters.jsCode:
const toolDescriptions = $input.first().json['Tool Descriptions'];
const lines = toolDescriptions.split('\n').filter(line => line.trim());
const tools = lines.map(line => {
  const match = line.match(/^([a-z_]+):\s*(.+)$/);
  if (match) return { name: match[1], description: match[2].trim() };
  return null;
}).filter(t => t !== null);

let projectName = $input.first().json['Project Name'].toLowerCase().replace(/\s+/g, '-');
if (!projectName.endsWith('-mcp')) projectName += '-mcp';

const langRaw = $input.first().json['Language'];
const sdk = langRaw.includes('Python') ? 'python' : 'typescript';
const version = sdk === 'python' ? '3.11+' : '18+';

return [{ json: { projectName, purpose: $input.first().json['One-Line Purpose'], sdk, version, targetService: $input.first().json['Target Service'], authType: $input.first().json['Auth Type'], apiDocsUrl: $input.first().json['API Docs URL'] || null, tools, toolCount: tools.length } }];

const data = $input.first().json;
const estimatedParams = data.toolCount * 2;
const tokens = (data.toolCount * 450) + (estimatedParams * 30);
let scope, scopeReason;
if (data.toolCount <= 5) { scope = 'project'; scopeReason = 'Lightweight MCP, install at project level'; }
else if (data.toolCount <= 10) { scope = 'project_or_user'; scopeReason = 'Consider project scope unless used across many projects'; }
else { scope = 'user_with_warning'; scopeReason = 'High overhead - consider splitting or user-level only'; }
let level;
if (tokens < 3500) level = 'optimal';
else if (tokens < 4500) level = 'acceptable';
else if (tokens < 7000) level = 'concerning';
else level = 'problematic';
return [{ json: { ...data, contextEfficiency: { toolCount: data.toolCount, estimatedTokens: tokens, level, scope, scopeReason } } }];

const data = $input.first().json;
const timestamp = new Date().toISOString().split('T')[0];
const toolsYaml = data.tools.map(tool => `
  - name: "${tool.name}"
    description: "${tool.description}"
    parameters: []
    returns: "Response data"`).join('');
const prdContent = `# ${data.projectName.toUpperCase()} - MCP PRD
# Generated by GRIMLOCK Design Wizard - ${timestamp}

version: "1.0"

project:
  name: "${data.projectName}"
  sdk: "${data.sdk}"
  ${data.sdk === 'python' ? `python_version: "${data.version}"` : `node_version: "${data.version}"`}

integration:
  service_name: "${data.targetService}"
  auth_type: "${data.authType}"
  ${data.apiDocsUrl ? `api_docs_url: "${data.apiDocsUrl}"` : '# api_docs_url: (not provided)'}

tools:${toolsYaml}

context_efficiency:
  tool_count: ${data.contextEfficiency.toolCount}
  estimated_tokens: ${data.contextEfficiency.estimatedTokens}
  level: "${data.contextEfficiency.level}"
  scope_recommendation: "${data.contextEfficiency.scope}"

design_origin:
  source: "form_wizard"
  interface: "n8n_form"
  design_date: "${timestamp}"
`;
return [{ json: { ...data, prdContent, prdFilename: `${data.projectName}-PRD.yaml` } }];

